import './App.css';
import React, { useState } from "react";

function App() {
  const [startText, setstartText] = useState("");
  const [rotateText, setrotateText] = useState("");
  const [result,setResult] = useState("");
  const [borderText, setborderText] = useState("");
  const [invText, setinvText] = useState("1");
  const [startTexterror, setstartTexterror] = useState("");
  const [Texterror, setTexterror] = useState("");
  //  const [borderTexterror, setborderTexterror] = useState("");
  const [buttonDisabled, setEbuttonDisabled] = useState(false);

  const onclickStart = () => {
    const sub = (Number((rotateText - startText) / invText) );
    if (!sub) {
      setResult("半角英数で入力してください");
    }
    else{
      setResult(sub);
    }
    setEbuttonDisabled(false)
    setstartText(rotateText)
    setrotateText("");
    setinvText(Number(invText) + 1)

  }

  const handleBlur = () => {
    const start = startText;
    const rotate = rotateText;
    const inv = invText;
    if (!start || !rotate || ! inv) {
      setTexterror("入力に不備があります")
      setEbuttonDisabled(false)
    }
    else {
       setTexterror("")
      setEbuttonDisabled(true)
    }
  }

  return (
    <>
    <div>
        スタート<input type="text"
          onChange={(e) => setstartText(e.target.value)}
          value={startText}
          onBlur={handleBlur}
        />
        1K後回転数<input type="text"
          onChange={(e) => setrotateText(e.target.value)}
          value={rotateText}
          onBlur={handleBlur}
        />
        投資金額<input type="text"
          onChange={(e) => setinvText(e.target.value)}
          value={invText}
          onBlur={handleBlur}
        />
      </div>
      <button type="button"
        onClick={onclickStart}
        disabled={!buttonDisabled}
        onBlur={handleBlur}>回転数計算</button>
      <p>{result}</p>
    </>
  )
}

export default App;
